+            {
+                o[0] = ff_reverse[(samples[0] & 0x000FF000) >> 12];
+                o[1] = ff_reverse[(samples[0] & 0x0FF00000) >> 20];
+                o[2] = ff_reverse[((samples[0] & 0xF0000000) >> 28) | vucf];
+                o[3] = ff_reverse[(samples[1] & 0x000FF000) >> 12];
