+void Entity::target(Terrain *terrain, coord::phys3 target) {
+	/*
+	 * discard all interruptible tasks
+	 */
+	for (auto action = action_stack.begin(); action != action_stack.end(); ++action) {
+		if ( (*action)->allow_interupt() ) {
+			action_stack.erase(action, action_stack.end());
+			break;
+		}
+	}
+
+	action_stack.push_back( std::make_shared<EntityMove>(this, terrain, target) );
+}
+
+void EntityAction::draw() {
+	/*
+	 * assume group has 5 image sets
+	 * mirroring is used to make additional image sets
+	 */
+	bool mirror = false;
+	uint angle = dirGroup(entity->dir);
+	if (angle > 4) {
+		angle = 8-angle;
+		mirror = true;
+	}
+
+	uint groupsize = tex->get_subtexture_count() / 5;
+	uint toDraw = angle * groupsize + ((uint) frame % groupsize);
+	tex->draw( entity->position.to_camgame(), mirror, toDraw, 1 );
+	frame += frame_rate;
+}
+
