+	coord::phys3 waypoint;
+	if (path.empty()) {
+		return;
+	}
+	else if (path.size() == 1) {
+		waypoint = target;
+	}
+	else {
+		waypoint = path.back().to_tile3().to_phys3();
+	}
+	coord::phys3_delta move_dir = waypoint - entity->position;
