+#ifndef OPENAGE_ENTITY_H_
+#define OPENAGE_ENTITY_H_
+
+#include <memory>
+#include <vector>
+
+#include "gamedata/graphic.gen.h"
+#include "gamedata/unit.gen.h"
+#include "pathfinding/path.h"
+#include "terrain.h"
+#include "texture.h"
+#include "util/dir.h"
+
+namespace openage {
+
+/**
+ * Stores graphics and attributes for a single unit type
+ */
+class EntityType {
+public:
+	EntityType(const gamedata::unit_living &,
+			   std::shared_ptr<Texture>,
+			   std::shared_ptr<Texture>,
+			   std::shared_ptr<Texture>);
+
+	virtual ~EntityType();
+
+	/**
+	 * list type attributes here
+	 */
+	std::shared_ptr<Texture> deadGraphic() const;
+	std::shared_ptr<Texture> idleGraphic() const;
+	std::shared_ptr<Texture> movingGraphic() const;
+	const gamedata::unit_living &data() const;
+
+private:
+	const gamedata::unit_living &unit_data;
+	std::shared_ptr<Texture> dead;
+	std::shared_ptr<Texture> idle;
+	std::shared_ptr<Texture> moving;
+};
+
+class EntityAction;
+
+/**
+ * a game object with current state represented by a stack of actions
+ */
+class Entity {
+public:
+	Entity(std::shared_ptr<EntityType>, const coord::phys3 &);
+	virtual ~Entity();
+
+	coord::phys3 position;
+	coord::phys3_delta dir;
+
+	/**
+	 * @return the type of this entity
+	 */
+	std::shared_ptr<EntityType> type() { return type_internal; }
+
+	/**
+	 * checks the entity has an action, if it has no action it should be removed from the game
+	 * @return true if the entity currently has an action
+	 */
+	bool has_action();
+
+	/**
+	 * update dispatched by the game engine
+	 */
+	void update();
+
+	/**
+	 * draw dispatched by the game engine
+	 */
+	void draw();
+
+	/**
+	 * discards all interruptible tasks and sets a new target
+	 * for this entity to move towards
+	 */
+	void target(Terrain *, coord::phys3);
+
+private:
+	const std::shared_ptr<EntityType> type_internal;
+
+	/**
+	 * action stack -- top action determines graphic to be drawn
+	 */
+	std::vector<std::shared_ptr<EntityAction>> action_stack;
+
+};
+
