+void Entity::update() {
+	/*
+	 * the active action is on top
+	 */
+	action_stack.back()->update();
+
+	/*
+	 * check completion of all actions,
+	 * pop completed actions and anything above
+	 */
+	for (auto action = action_stack.begin(); action != action_stack.end(); ++action) {
+		if ( (*action)->completed() ) {
+			action_stack.erase(action, action_stack.end());
+			break;
+		}
