+	/*
+	 * discard all interruptible tasks
+	 */
+	for (auto action = action_stack.begin(); action != action_stack.end(); ++action) {
+		if ( (*action)->allow_interupt() ) {
+			action_stack.erase(action, action_stack.end());
+			break;
+		}
+	}
+
+	action_stack.push_back( std::make_shared<EntityMove>(this, terrain, target) );
+}
