+void EntityAction::draw() {
+	/*
+	 * assume group has 5 image sets
+	 * mirroring is used to make additional image sets
+	 */
+	bool mirror = false;
+	uint angle = dirGroup(entity->dir);
+	if (angle > 4) {
+		angle = 8-angle;
+		mirror = true;
+	}
+
+	uint groupsize = tex->get_subtexture_count() / 5;
+	uint toDraw = angle * groupsize + ((uint) frame % groupsize);
+	tex->draw( entity->position.to_camgame(), mirror, toDraw, 1 );
+	frame += frame_rate;
+}
+
+void EntityMove::update() {
+	/*
+	 * find a point to move directly towards
+	 */
+	coord::phys3 waypoint;
+	if (path.empty()) {
+		return;
+	}
+	else if (path.size() == 1) {
+		waypoint = target;
+	}
+	else {
+		waypoint = path.back().to_tile3().to_phys3();
+	}
+	coord::phys3_delta move_dir = waypoint - entity->position;
+
+	// normalise dir
+	double distance = (coord::phys_t) sqrt(move_dir.ne*move_dir.ne + move_dir.se*move_dir.se);
+
+	// speed adjusted by type -- and avoid divide by zero
+	coord::phys_t speed = entity->type()->data().speed * (1 << 12);
+	move_dir /= 1 + (distance / speed);
+
+
+	// change entity position and direction
+	entity->position += move_dir;
+	entity->dir = move_dir;
+
+	if (distance < 10000) {
+		path.pop_back();
+	}
+}
+
+bool EntityMove::completed() {
+	if (path.empty()) {
+		return true;
+	}
+
+	coord::phys3_delta move_dir = target - entity->position;
+
+	// normalise dir
+	distance_to_target = (coord::phys_t) sqrt(move_dir.ne*move_dir.ne + move_dir.se*move_dir.se);
+
+	/*
+	 * close enough -- for testing
+	 */
+	if (distance_to_target < 10000) {
+		return true;
+	}
+	return false;
+}
