+		return (tc->terrain_id != 1 && tc->terrain_id != 14 && tc->terrain_id != 15 && tc->obj == nullptr);
+	};
+	coord::tile start = entity->position.to_tile3().to_tile();
+	coord::tile end = target.to_tile3().to_tile();
+	path = path::a_star(start, end, path::euclidean_cost, f);
+}
