+// called on core shutdown to release all exchanges
+//
+void qdr_exchange_free_all(qdr_core_t *core)
+{
+    qdr_exchange_t *ex = DEQ_HEAD(core->exchanges);
+    while (ex) {
+        qdr_exchange_t *next = DEQ_NEXT(ex);
+        qdr_exchange_free(ex);
+        ex = next;
+    }
+}
