+
+            Span span = GenerateSpan(@"    @if (true)", SpanKind.Code, 2, "if (true)");
+
+            var paddingBuilder = new CSharpPaddingBuilder(host);
+
+            int padding = paddingBuilder.CalculatePadding(span);
+
+            Assert.Equal(5, padding);
