import com.amazonaws.AmazonClientException;
import com.amazonaws.auth.AWSCredentials;
import com.amazonaws.auth.BasicAWSCredentials;
import com.amazonaws.services.s3.model.CannedAccessControlList;
import com.amazonaws.services.s3.model.ProgressEvent;
import com.amazonaws.services.s3.model.ProgressListener;
import com.amazonaws.services.s3.model.PutObjectRequest;
import com.amazonaws.services.s3.transfer.TransferManager;
import com.amazonaws.services.s3.transfer.Upload;
import com.amazonaws.services.s3.transfer.model.UploadResult;

import java.io.File;

public class S3TransferProgressSample
{
    String accessKey = "access";
    String secretKey = "secret";
    String bucketName = "bucketname";
    String fileToUpload = "/tmp/LargerThan5Mb.txt";
    private Upload s3Upload;

```
public static void main(String[] args)
{
    new S3TransferProgressSample();
}

public S3TransferProgressSample()
{
    try
    {
        AWSCredentials myCredentials = new BasicAWSCredentials(accessKey, secretKey);
        TransferManager tx = new TransferManager(myCredentials);
        File file = new File(fileToUpload);

        //Define a progress listener for our upload
        ProgressListener progressListener = new ProgressListener()
        {
            @Override
            public void progressChanged(ProgressEvent progressEvent)
            {
                if (s3Upload == null)
                {
                    System.out.println("s3Upload was NULL!!!");
                    return;
                }                                       

                switch (progressEvent.getEventCode())
                {
                    case ProgressEvent.COMPLETED_EVENT_CODE:
                    {
                        System.out.println("S3 Progress Event " + progressEvent.getEventCode() + " % Complete: " + s3Upload.getProgress().getPercentTransferred());
                        System.out.println("S3 COMPLETED_EVENT_CODE s3Upload.isDone(): " + s3Upload.isDone());
                        try
                        {
                            System.out.println("Waiting for upload result...");
                            UploadResult result = s3Upload.waitForUploadResult();
                            if (result != null)
                            {
                                String uploadedS3Filename = result.getKey();
                                System.out.println("File uploaded with key: " + uploadedS3Filename);
                            }
                        }
                        catch (Throwable e)
                        {
                            System.out.println("Throwable during S3 waitForUploadResult.");
                            e.printStackTrace();
                        }

                        System.out.println("S3 UPLOAD COMPLETE.");
                        break;
                    }
                    case ProgressEvent.STARTED_EVENT_CODE:
                    {
                        System.out.println("S3 STARTED_EVENT_CODE");
                        System.out.println("S3 Progress Event " + progressEvent.getEventCode() + " % Complete: " + s3Upload.getProgress().getPercentTransferred());
                        break;
                    }
                    case ProgressEvent.FAILED_EVENT_CODE:
                    {
                        System.out.println("S3 FAILED_EVENT_CODE");
                        System.out.println("S3 Progress Event " + progressEvent.getEventCode() + " % Complete: " + s3Upload.getProgress().getPercentTransferred());
                        try
                        {
                            AmazonClientException e = s3Upload.waitForException();
                            System.err.println("S3 upload failed!");
                        }
                        catch (Throwable e)
                        {
                            System.err.println("Throwable during S3 upload.");
                        }
                        break;
                    }
                }
            }
        };

        PutObjectRequest request = new PutObjectRequest(bucketName, file.getName(), file)
                .withProgressListener(progressListener)
                .withCannedAcl(CannedAccessControlList.PublicRead);

        //This starts the upload thread but the listener above controls what happens after this
        s3Upload = tx.upload(request);
    }
    catch (Throwable e)
    {
        e.printStackTrace();
    }
}
```

