+
+        private Dictionary<string, Library> builtLibs = new Dictionary<string, Library>();
+
+        /// <summary>
+        /// Scans the binary directory to get the built libraries.
+        /// Then iterates recursively on the tests directoriesm, when it founds a mathing library it 
+        /// overwrites it with the one from the bin directory.
+        /// </summary>
+        /// <returns></returns>
+        public override bool Execute()
+        {
+            var libDirs = Directory.GetDirectories(BinPath)
+                            .Where((x) => 
+                            {
+                                //exclude locally built tests and xunit related libs
+                                return !x.EndsWith(".Tests") && x.IndexOf("xunit", StringComparison.OrdinalIgnoreCase) < 0; 
+                            });
