+            foreach (var dir in libDirs)
+            {
+                string fileName = dir.Substring(dir.LastIndexOf('\\') + 1);
+                string dllPath = Path.Combine(dir, fileName + ".dll");
+                string pdbPath = Path.Combine(dir, fileName + ".pdb");
+
+                if (!builtLibs.ContainsKey(fileName))
+                {
+                    if (File.Exists(dllPath) && File.Exists(pdbPath))
+                        builtLibs.Add(fileName, new Library(dllPath, pdbPath));
+                }
+                else
+                {
+                    Log.LogWarning("Warning, library {0} was already added. Make sure the right one is being used.", fileName);
+                }
+            }
+
+            foreach (var lib in Directory.GetFiles(TestOutputPath))
+            {
+                string fileName = Path.GetFileNameWithoutExtension(lib);
+                if (builtLibs.ContainsKey(fileName))
+                {
+                    Log.LogMessage("Replacing {0} with {1} and {2}", lib, builtLibs[fileName].DllPath, builtLibs[fileName].PdbPath);
+                    File.Copy(builtLibs[fileName].DllPath, Path.Combine(TestOutputPath, fileName + ".dll"), true);
+                    File.Copy(builtLibs[fileName].PdbPath, Path.Combine(TestOutputPath, fileName + ".pdb"), true);
+                }
+            }
+
+            return true;
+        }
