+  for (CFIndex i = 0; i < CFArrayGetCount((CFArrayRef) kid); i++) {
+    kid_dict = (CFDictionaryRef) CFArrayGetValueAtIndex((CFArrayRef) kid, i);
+    kid_value = (const char*) CFDictionaryGetValue(kid_dict, kSecPropertyKeyValue);
