+// heavily influenced by github.com/kristrev/inet-diag-example
+enum{
+    TCP_ESTABLISHED = 1,
+    TCP_SYN_SENT,
+    TCP_SYN_RECV,
+    TCP_FIN_WAIT1,
+    TCP_FIN_WAIT2,
+    TCP_TIME_WAIT,
+    TCP_CLOSE,
+    TCP_CLOSE_WAIT,
+    TCP_LAST_ACK,
+    TCP_LISTEN,
+    TCP_CLOSING
+};
+
+#define TCPF_ALL 0xFFF
+#define SOCKET_BUFFER_SIZE (getpagesize() < 8192L ? getpagesize() : 8192L)
+
+int send_diag_msg(int sockfd, int family){
+    struct msghdr msg;
+    struct nlmsghdr nlh;
+    struct inet_diag_req_v2 conn_req;
+    struct sockaddr_nl sa;
+    struct iovec iov[4];
+    int retval = 0;
+
+    memset(&msg, 0, sizeof(msg));
+    memset(&sa, 0, sizeof(sa));
+    memset(&nlh, 0, sizeof(nlh));
+    memset(&conn_req, 0, sizeof(conn_req));
+
+    sa.nl_family = AF_NETLINK;
+
+    if (family == 1) {
+        conn_req.sdiag_family = AF_INET;
+    } else if (family == 2) {
+        conn_req.sdiag_family = AF_INET6;
+    }
+    conn_req.sdiag_protocol = IPPROTO_TCP;
+
+    conn_req.idiag_states = TCPF_ALL &
+        ~((1<<TCP_SYN_RECV) | (1<<TCP_TIME_WAIT) | (1<<TCP_CLOSE));
+
+    conn_req.idiag_ext |= (1 << (INET_DIAG_INFO - 1));
+
+    nlh.nlmsg_len = NLMSG_LENGTH(sizeof(conn_req));
+    nlh.nlmsg_flags = NLM_F_DUMP | NLM_F_REQUEST;
+
+    nlh.nlmsg_type = SOCK_DIAG_BY_FAMILY;
+    iov[0].iov_base = (void*) &nlh;
+    iov[0].iov_len = sizeof(nlh);
+    iov[1].iov_base = (void*) &conn_req;
+    iov[1].iov_len = sizeof(conn_req);
+
+    msg.msg_name = (void*) &sa;
+    msg.msg_namelen = sizeof(sa);
+    msg.msg_iov = iov;
+    msg.msg_iovlen = 2;
+
+    retval = sendmsg(sockfd, &msg, 0);
+
+    return retval;
+}
