  const uint64 hi_left_one  = ( hi << 1 );
  const uint64 lo_left_nine_eight_c = ( lo << UINT8_C( 9 ) ); //to see if it's the 32-bit value of the integer literal
  const uint64 hi_left_one_eight_c  = ( hi << UINT8_C( 1 ) );
  const uint64 lo_left_nine_eight_c = ( lo << uint8( 9 ) );   //to see if it's the 32-bit value of the integer literal
  const uint64 hi_left_one_eight_c  = ( hi << uint8( 1 ) ); 
```

