+static GF_Err MCDec_DetachStream(GF_BaseDecoder *ifcg, u16 ES_ID)
+{
+    MCDec *ctx = (MCDec *)ifcg->privateStack;
+    
+#ifdef GPAC_ANDROID
+
+    if(AMediaCodec_stop(ctx->codec) != AMEDIA_OK) {
+        LOGE("AMediaCodec_stop failed");
+    }
+
+#endif
+
+    return GF_OK;
+}
