+//prepend the inBuffer with the start code i.e. 0x00 0x00 0x00 0x01
+void prependStartCode(char *inBuffer, char *outBuffer, u32 *size) 
+{
+    *size += 4;
+    u32 i;
+                              
+    for(i = 0; i < *size; i++) {
+        if(i < 3)
+            outBuffer[i] = 0x00;   
+        
+        else if (i == 3) 
+            outBuffer[i] = 0x01;
+            
+        else  outBuffer[i] = inBuffer[i-4];
+    }
+}
