+u32 androidPixelFormat(u32 pix_fmt) {
+
+    switch(pix_fmt) {
+        case GF_PIXEL_YV12:
+            return 0x32315659;
+
+        case GF_PIXEL_YUV422:
+                return 0x27;
+
+        case GF_PIXEL_YUV444:
+                return 0x28;
+
+        case GF_PIXEL_NV21:
+                return 0x11;
+
+        case GF_PIXEL_RGB_565:
+                return 0x04;
+
+        case GF_PIXEL_YUY2:
+                return 0x14;
+
+        case GF_PIXEL_RGB_32:
+                return 0x2a;
+
+        case GF_PIXEL_RGB_24:
+                return 0x29;
+
+        case GF_PIXEL_YUV444_10:
+        case GF_PIXEL_YV12_10:
