+static inline void forward_data(h2o_socket_t *dst, h2o_socket_t *src, h2o_socket_cb cb)
+{
+    h2o_iovec_t buf;
+    buf.base = src->input->bytes;
+    buf.len = src->input->size;
+    h2o_socket_write(dst, &buf, 1, cb);
