        Join("TABLE_2", j =>
        {
            j.Schema("SCHEMA_1");
            j.Optional();
            j.Fetch.Join();

            j.KeyColumn("XYZ");

            // TABLE_2 is a linking table between TABLE_1 and TABLE_3.
            // assume fields: TABLE_2.XYZ,  TABLE_2.ABC

