+package org.jenkinsci.plugins.github.config.GitHubPluginConfig
+
+import com.cloudbees.jenkins.GitHubPushTrigger
+
+def f = namespace(lib.FormTagLib);
+
+f.section(title: descriptor.displayName) {
+    if (GitHubPushTrigger.ALLOW_HOOKURL_OVERRIDE) {
+        f.entry(title: _("Override Hook URL")) {
+            table(width: "100%", style: "margin-left: 7px;") {
+                f.optionalBlock(title: _("Specify another hook url"), inline: false, field: "overrideHookUrl", checked: instance.overrideHookURL) {
+                    f.entry(field: "hookUrl") {
+                        f.textbox()
+                    }
+                }
+            }
+        }
+    }
+
+    f.entry(title: _("Credentials to manage GitHub webhooks"), description: _("List of GitHub Servers to connect with given credentials")) {
+        f.repeatableHeteroProperty(
+                field: "configs",
+                hasHeader: "true",
+                addCaption: _("Add GitHub Server Credentials"),
+                deleteCaption: _("Delete Credentials"))
+    }
+
+    if (instance.manageHooks) {
+        f.validateButton(
+                title: _("Re-register hooks for all jobs"),
