+                    if (val.diskLoc.file > lastDiskLoc.file) {
+                        print("Warning: "+name+" diskLoc file: "+
+                              val.diskLoc.file+" > "+lastDiskLoc.file);
+                        printjson(val);
+                    }
+                    if (val.diskLoc.offset >= lastDiskLoc.offset) {
+                        print("Warning: "+name+" diskLoc file: "+
+                              val.diskLoc.offset+" >= "+lastDiskLoc.offset);
