+                                  <label for="{{ option }}" >{{ option }}</label>
+                                  <input type='radio' id="{{ option }}"
+                                         name="{{ setting.name }}" value="{{ option }}"
+                                         {% if value.value==option %}checked{% endif %}>
+                                </li>
+                              {% endfor %}
+                             </ul>
+                           </fieldset>
+                        {% endif %}
