-#if (BOOST_VERSION != 104800) || (BOOST_VERSION != 104900)
+#if (BOOST_VERSION/100 != 1048) || (BOOST_VERSION/100 != 1049)
 namespace BOOST_FOREACH = foreach;
 #endif

