+
+	@MessagingGateway(defaultRequestChannel = "toTcp.input")
+	public interface ToTCP {
+
+		public void send(String data, @Header("host") String host, @Header("port") int port);
+
+	}
+
+	@Bean
+	public IntegrationFlow toTcp() {
+		return f -> f.route(router());
