+	}
+
+	@Bean
+	public TcpNetServerConnectionFactory cfTwo() {
+		return new TcpNetServerConnectionFactory(5678);
+	}
+
+	@Bean
+	public QueueChannel outputChannel() {
+		return new QueueChannel();
+	}
