        jedisConnectionFactory.setUsePool(Boolean.parseBoolean(System
            .getProperty("spring_session_redis_use_pool")));
        return jedisConnectionFactory;
      }

      @Bean
      public static JedisPoolConfig jedisPoolConfig() {
        JedisPoolConfig jedisPoolConfig = new JedisPoolConfig();
        jedisPoolConfig.setTestOnBorrow(Boolean.parseBoolean(System
            .getProperty("spring_session_redis_pool_test_on_borrow")));
        jedisPoolConfig.setMaxTotal(Integer.parseInt(System
            .getProperty("spring_session_redis_pool_max_total")));
        jedisPoolConfig.setMaxIdle(Integer.parseInt(System
            .getProperty("spring_session_redis_pool_max_idle")));
        jedisPoolConfig.setMinIdle(Integer.parseInt(System
            .getProperty("spring_session_redis_pool_min_idle")));
        return jedisPoolConfig;
      }

    }
