Original Code

```
class A
        {
            public int F1;
            private int F2;
            protected int F3;
            public static int SF;

            public int P1 { get; set; }
            private int P2 { get; set; }
            static int SP { get; set; }

            public A()
            {
                F2 = 2;
                F3 = 3;
            }
        }

        class B
        {
            public int F1;
            public int F2;
            public int F3;
            public static int SF;

            public int P1 { get; set; }
            public int P2 { get; set; }
            public int P3 { get; set; }
            static int SP { get; set; }
        }

        [Fact]
        public void Test_Extensions_Generic_Map()
        {
            var A = new A
            {
                F1 = 1,
                P1 = 1
            };
            A.SF = 4;
            var B = A.Map<A, B>();
            Assert.Equal(1, B.F1);
            Assert.Equal(2, B.F2);
            Assert.Equal(3, B.F3);
            Assert.Equal(4, B.SF);
            Assert.Equal(1, B.P1);
            Assert.Equal(0, B.P2);
            Assert.Equal(0, B.P3);

            B = new B
            {
                F1 = 2,
                F2 = 1
            };
            B.Map(A, "F1", "p1");
            Assert.Equal(2, B.F2);
            Assert.Equal(3, B.F3);
            Assert.Equal(4, B.SF);
            Assert.Equal(0, B.P1);
            Assert.Equal(0, B.P2);
            Assert.Equal(0, B.P3);
        }
